import { GoogleGenAI } from "@google/genai";
import { PROFILE, PROJECTS, EXPERIENCE, EDUCATION, SKILLS } from '../constants';

const API_KEY = process.env.API_KEY || ''; // Ensure this is set in your environment
const ai = new GoogleGenAI({ apiKey: API_KEY });

// Construct a system prompt containing the architect's data
const systemInstruction = `
Eres el asistente virtual IA del portafolio del Arquitecto ${PROFILE.name}.
Tu objetivo es responder preguntas de posibles clientes, reclutadores o estudiantes sobre la carrera, proyectos y estilo de Mateo.
Usa la siguiente informaci√≥n como tu base de conocimiento (Ground Truth):

Perfil: ${JSON.stringify(PROFILE)}
Experiencia: ${JSON.stringify(EXPERIENCE)}
Educaci√≥n: ${JSON.stringify(EDUCATION)}
Habilidades: ${JSON.stringify(SKILLS)}
Proyectos Destacados: ${JSON.stringify(PROJECTS.map(p => ({ title: p.title, description: p.description, tech: p.tags })))}

Reglas:
1. Responde siempre de manera profesional, educada y concisa.
2. Si te preguntan por contacto, da el email o tel√©fono del perfil.
3. Si te preguntan algo que no est√° en la informaci√≥n, responde que no tienes ese dato pero sugieres contactar directamente al arquitecto.
4. Mant√©n un tono elegante y sofisticado, acorde a un arquitecto moderno.
5. Responde en el idioma en que te pregunten (detecta si es Ingl√©s o Espa√±ol).
`;

export const sendMessageToGemini = async (history: {role: 'user' | 'model', content: string}[], message: string) => {
  try {
    if (!API_KEY) {
      return "Lo siento, la API Key no est√° configurada en este demo.";
    }

    const model = 'gemini-3-flash-preview'; 

    // Convert history to format expected by GoogleGenAI if necessary, 
    // but for simple single-turn or manual history management with generateContent, 
    // we can just append the new message to a chat session.

    // Using chat for context retention
    const chat = ai.chats.create({
      model: model,
      config: {
        systemInstruction: systemInstruction,
      },
      history: history.map(h => ({
        role: h.role,
        parts: [{ text: h.content }]
      }))
    });

    const result = await chat.sendMessage({
      message: message
    });

    return result.text;
  } catch (error) {
    console.error("Gemini Error:", error);
    return "Tuve un problema t√©cnico moment√°neo. Por favor intenta de nuevo.";
  }
};

import React, { useState } from 'react';
import { Project, ProjectImage } from '../types';
import { X, Layers, Image as ImageIcon, Map, ChevronLeft, ChevronRight, Maximize2 } from 'lucide-react';

interface ProjectModalProps {
  project: Project;
  onClose: () => void;
}

const ProjectModal: React.FC<ProjectModalProps> = ({ project, onClose }) => {
  const [activeTab, setActiveTab] = useState<'all' | 'render' | 'plan' | 'detail'>('all');
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [isFullScreen, setIsFullScreen] = useState(false);

  // Filter images based on tab
  const filteredImages = activeTab === 'all' 
    ? project.images 
    : project.images.filter(img => img.type === activeTab);

  // Use filtered images for the main view, fallback to first if filter empties selection
  const currentImages = filteredImages.length > 0 ? filteredImages : project.images;
  const currentImage = currentImages[selectedIndex] || currentImages[0];

  const handleNext = () => {
    setSelectedIndex((prev) => (prev + 1) % currentImages.length);
  };

  const handlePrev = () => {
    setSelectedIndex((prev) => (prev - 1 + currentImages.length) % currentImages.length);
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/90 backdrop-blur-sm animate-fade-in">
      {/* Close Button */}
      <button 
        onClick={onClose}
        className="absolute top-6 right-6 text-white hover:text-arch-accent transition-colors z-50"
      >
        <X size={32} />
      </button>

      <div className={`bg-arch-dark w-full max-w-6xl rounded-lg overflow-hidden flex flex-col md:flex-row shadow-2xl border border-slate-800 ${isFullScreen ? 'h-full' : 'max-h-[90vh]'}`}>

        {/* Main Viewer Area */}
        <div className="relative flex-1 bg-black flex items-center justify-center group overflow-hidden">

          <img 
            src={currentImage.url} 
            alt={currentImage.caption}
            className={`object-contain w-full h-full transition-transform duration-700 ${isFullScreen ? 'scale-100' : ''}`}
          />

          {/* Overlay Gradient */}
          <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-transparent to-transparent opacity-60"></div>

          {/* Navigation Arrows */}
          <button 
            onClick={handlePrev}
            className="absolute left-4 p-2 bg-black/50 text-white rounded-full hover:bg-arch-accent transition-colors opacity-0 group-hover:opacity-100"
          >
            <ChevronLeft size={24} />
          </button>
          <button 
            onClick={handleNext}
            className="absolute right-4 p-2 bg-black/50 text-white rounded-full hover:bg-arch-accent transition-colors opacity-0 group-hover:opacity-100"
          >
            <ChevronRight size={24} />
          </button>

          {/* Image Caption & Type */}
          <div className="absolute bottom-6 left-6 right-6">
            <span className="inline-block px-2 py-1 text-xs font-bold tracking-wider text-black bg-arch-accent uppercase rounded-sm mb-2">
              {currentImage.type}
            </span>
            <h3 className="text-xl md:text-2xl font-display font-medium text-white">{currentImage.caption}</h3>
          </div>

          <button 
             onClick={() => setIsFullScreen(!isFullScreen)}
             className="absolute top-4 right-4 text-white/50 hover:text-white"
          >
            <Maximize2 size={20}/>
          </button>
        </div>

        {/* Sidebar / Info Panel */}
        <div className="w-full md:w-96 bg-slate-900 p-6 flex flex-col border-l border-slate-800 overflow-y-auto">

          <div className="mb-6">
            <h2 className="text-3xl font-display font-bold text-white mb-2">{project.title}</h2>
            <div className="text-arch-accent text-sm font-medium mb-4">{project.location} | {project.year}</div>
            <p className="text-slate-400 text-sm leading-relaxed mb-4">
              {project.description}
            </p>
            <div className="flex flex-wrap gap-2">
              {project.tags.map(tag => (
                <span key={tag} className="text-xs border border-slate-700 text-slate-300 px-2 py-1 rounded-full">
                  {tag}
                </span>
              ))}
            </div>
          </div>

          {/* View Controls */}
          <div className="mt-auto">
            <h4 className="text-xs font-bold text-slate-500 uppercase tracking-widest mb-3">Modo de Visualizaci√≥n</h4>
            <div className="grid grid-cols-4 gap-2 mb-4">
               <button 
                onClick={() => { setActiveTab('all'); setSelectedIndex(0); }}
                className={`flex flex-col items-center justify-center p-2 rounded-md transition-all ${activeTab === 'all' ? 'bg-slate-800 text-arch-accent ring-1 ring-arch-accent' : 'bg-slate-800/50 text-slate-400 hover:bg-slate-800'}`}
               >
                 <Layers size={18} className="mb-1" />
                 <span className="text-[10px]">Todo</span>
               </button>
               <button 
                onClick={() => { setActiveTab('render'); setSelectedIndex(0); }}
                className={`flex flex-col items-center justify-center p-2 rounded-md transition-all ${activeTab === 'render' ? 'bg-slate-800 text-arch-accent ring-1 ring-arch-accent' : 'bg-slate-800/50 text-slate-400 hover:bg-slate-800'}`}
               >
                 <ImageIcon size={18} className="mb-1" />
                 <span className="text-[10px]">Renders</span>
               </button>
               <button 
                onClick={() => { setActiveTab('plan'); setSelectedIndex(0); }}
                className={`flex flex-col items-center justify-center p-2 rounded-md transition-all ${activeTab === 'plan' ? 'bg-slate-800 text-arch-accent ring-1 ring-arch-accent' : 'bg-slate-800/50 text-slate-400 hover:bg-slate-800'}`}
               >
                 <Map size={18} className="mb-1" />
                 <span className="text-[10px]">Planos</span>
               </button>
               <button 
                onClick={() => { setActiveTab('detail'); setSelectedIndex(0); }}
                className={`flex flex-col items-center justify-center p-2 rounded-md transition-all ${activeTab === 'detail' ? 'bg-slate-800 text-arch-accent ring-1 ring-arch-accent' : 'bg-slate-800/50 text-slate-400 hover:bg-slate-800'}`}
               >
                 <Maximize2 size={18} className="mb-1" />
                 <span className="text-[10px]">Detalle</span>
               </button>
            </div>

            {/* Thumbnail Grid */}
            <h4 className="text-xs font-bold text-slate-500 uppercase tracking-widest mb-2">Galer√≠a</h4>
            <div className="grid grid-cols-3 gap-2">
              {currentImages.map((img, idx) => (
                <button 
                  key={idx}
                  onClick={() => setSelectedIndex(idx)}
                  className={`relative aspect-square overflow-hidden rounded-md ${selectedIndex === idx ? 'ring-2 ring-arch-accent' : 'opacity-60 hover:opacity-100'}`}
                >
                  <img src={img.url} className="w-full h-full object-cover" alt="thumbnail" />
                </button>
              ))}
            </div>
          </div>

        </div>
      </div>
    </div>
  );
};

export default ProjectModal;

import React, { useState, useRef, useEffect } from 'react';
import { MessageSquare, X, Send, Bot, Loader2 } from 'lucide-react';
import { sendMessageToGemini } from '../services/geminiService';
import { PROFILE } from '../constants';

const AIChat: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState<{role: 'user' | 'model', content: string}[]>([
    { role: 'model', content: `Hola, soy el asistente virtual de ${PROFILE.name}. ¬øEn qu√© puedo ayudarte hoy?` }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (e?: React.FormEvent) => {
    e?.preventDefault();
    if (!inputValue.trim() || isLoading) return;

    const userMsg = inputValue;
    setInputValue('');
    setMessages(prev => [...prev, { role: 'user', content: userMsg }]);
    setIsLoading(true);

    const response = await sendMessageToGemini(messages, userMsg);

    setMessages(prev => [...prev, { role: 'model', content: response || "Error de conexi√≥n." }]);
    setIsLoading(false);
  };

  return (
    <div className="fixed bottom-6 right-6 z-40 flex flex-col items-end">

      {/* Chat Window */}
      {isOpen && (
        <div className="mb-4 w-80 md:w-96 bg-slate-900 border border-slate-700 rounded-lg shadow-2xl overflow-hidden animate-slide-up flex flex-col h-[500px]">
          {/* Header */}
          <div className="bg-slate-800 p-4 flex items-center justify-between border-b border-slate-700">
            <div className="flex items-center space-x-2">
              <div className="bg-arch-accent p-1.5 rounded-full">
                <Bot size={16} className="text-black" />
              </div>
              <div>
                <h3 className="text-white font-bold text-sm">Asistente IA</h3>
                <span className="text-xs text-green-400 flex items-center gap-1">
                  <span className="w-1.5 h-1.5 bg-green-400 rounded-full animate-pulse"></span> Online
                </span>
              </div>
            </div>
            <button onClick={() => setIsOpen(false)} className="text-slate-400 hover:text-white">
              <X size={18} />
            </button>
          </div>

          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-slate-900/95">
            {messages.map((msg, idx) => (
              <div key={idx} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className={`max-w-[85%] p-3 rounded-lg text-sm leading-relaxed ${
                  msg.role === 'user' 
                    ? 'bg-arch-accent text-slate-900 font-medium rounded-tr-none' 
                    : 'bg-slate-800 text-slate-200 rounded-tl-none border border-slate-700'
                }`}>
                  {msg.content}
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="flex justify-start">
                <div className="bg-slate-800 p-3 rounded-lg rounded-tl-none border border-slate-700">
                  <Loader2 size={16} className="animate-spin text-arch-accent" />
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <form onSubmit={handleSend} className="p-3 bg-slate-800 border-t border-slate-700 flex gap-2">
            <input 
              type="text" 
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              placeholder="Pregunta sobre proyectos, costos..." 
              className="flex-1 bg-slate-900 text-white text-sm rounded-md px-3 py-2 border border-slate-700 focus:outline-none focus:border-arch-accent transition-colors"
            />
            <button 
              type="submit"
              disabled={isLoading || !inputValue.trim()}
              className="bg-arch-accent text-slate-900 p-2 rounded-md hover:bg-yellow-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <Send size={18} />
            </button>
          </form>
        </div>
      )}

      {/* Toggle Button */}
      <button 
        onClick={() => setIsOpen(!isOpen)}
        className="group flex items-center gap-2 bg-arch-accent hover:bg-yellow-600 text-slate-900 font-bold py-3 px-4 rounded-full shadow-lg transition-all transform hover:scale-105"
      >
        <span className={`${isOpen ? 'hidden' : 'hidden md:block'}`}>Preguntar a IA</span>
        {isOpen ? <X size={24} /> : <MessageSquare size={24} />}
      </button>
    </div>
  );
};

export default AIChat;

import React, { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';
import { PROFILE } from '../constants';

const Navbar: React.FC = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navLinks = [
    { name: 'Inicio', href: '#home' },
    { name: 'Proyectos', href: '#projects' },
    { name: 'Trayectoria', href: '#experience' },
    { name: 'Estudios', href: '#education' },
    { name: 'Contacto', href: '#contact' },
  ];

  return (
    <nav className={`fixed top-0 left-0 right-0 z-40 transition-all duration-300 ${scrolled ? 'bg-arch-dark/95 backdrop-blur-md py-4 border-b border-white/5' : 'bg-transparent py-6'}`}>
      <div className="container mx-auto px-6 flex justify-between items-center">

        {/* Logo */}
        <a href="#" className="text-2xl font-display font-bold tracking-tighter text-white">
          MV<span className="text-arch-accent">.</span>ARCH
        </a>

        {/* Desktop Menu */}
        <div className="hidden md:flex space-x-8">
          {navLinks.map((link) => (
            <a 
              key={link.name} 
              href={link.href} 
              className="text-sm font-medium text-slate-300 hover:text-arch-accent transition-colors uppercase tracking-widest"
            >
              {link.name}
            </a>
          ))}
        </div>

        {/* Mobile Toggle */}
        <button 
          className="md:hidden text-white hover:text-arch-accent"
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
        >
          {mobileMenuOpen ? <X /> : <Menu />}
        </button>
      </div>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="md:hidden absolute top-full left-0 right-0 bg-arch-dark border-b border-white/5 p-6 flex flex-col space-y-4 animate-fade-in shadow-xl">
          {navLinks.map((link) => (
            <a 
              key={link.name} 
              href={link.href}
              onClick={() => setMobileMenuOpen(false)}
              className="text-lg font-medium text-slate-300 hover:text-arch-accent"
            >
              {link.name}
            </a>
          ))}
        </div>
      )}
    </nav>
  );
};

