                <h3 className="text-2xl font-bold text-white">Cursos & Certificaciones</h3>
                <button onClick={() => setEditingCourse({} as Course)} className="text-arch-accent hover:text-white flex items-center gap-1 font-bold">
                  <Plus size={16}/> A√±adir
                </button>
              </div>

              {editingCourse && renderCourseForm()}

              <div className="grid gap-4">
                {courses.map(course => (
                  <div key={course.id} className="bg-slate-800 p-4 rounded border border-slate-700 flex justify-between items-center group">
                     <div>
                       <h4 className="font-bold text-white">{course.name}</h4>
                       <p className="text-slate-400 text-sm">{course.institution} <span className="text-arch-accent ml-2">{course.year}</span></p>
                     </div>
                     <div className="flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                        <button onClick={() => setEditingCourse(course)} className="p-1.5 bg-slate-700 rounded hover:bg-white hover:text-black"><Edit2 size={14}/></button>
                        <button onClick={() => { if(confirm('¬øBorrar curso?')) deleteCourse(course.id) }} className="p-1.5 bg-slate-700 text-red-400 rounded hover:bg-red-500 hover:text-white"><Trash2 size={14}/></button>
                     </div>
                  </div>
                ))}
                {courses.length === 0 && <p className="text-slate-500 italic">No hay cursos registrados.</p>}
              </div>
            </section>

          </div>
        )}

      </div>
    </div>
  );
};

