import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { Project, Experience, Education, Course, Skill, ArchitectProfile } from '../types';
import { PROJECTS as INITIAL_PROJECTS, EXPERIENCE as INITIAL_EXPERIENCE, EDUCATION as INITIAL_EDUCATION, COURSES as INITIAL_COURSES, SKILLS as INITIAL_SKILLS, PROFILE as INITIAL_PROFILE } from '../constants';

interface DataContextType {
  profile: ArchitectProfile;
  projects: Project[];
  experience: Experience[];
  education: Education[];
  courses: Course[];
  skills: Skill[];
  isAdmin: boolean;
  login: (password: string) => boolean;
  logout: () => void;
  updateProfile: (profile: ArchitectProfile) => void;
  addProject: (project: Project) => void;
  updateProject: (project: Project) => void;
  deleteProject: (id: string) => void;
  addExperience: (exp: Experience) => void;
  updateExperience: (exp: Experience) => void;
  deleteExperience: (id: string) => void;
  addEducation: (edu: Education) => void;
  updateEducation: (edu: Education) => void;
  deleteEducation: (id: string) => void;
  addCourse: (course: Course) => void;
  updateCourse: (course: Course) => void;
  deleteCourse: (id: string) => void;
  addSkill: (skill: Skill) => void;
  updateSkill: (skill: Skill) => void;
  deleteSkill: (id: string) => void;
  resetData: () => void;
}

const DataContext = createContext<DataContextType | undefined>(undefined);

export const DataProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  // Initialize state from LocalStorage or fall back to Constants
  const [profile, setProfile] = useState<ArchitectProfile>(() => {
    const saved = localStorage.getItem('arch_profile');
    return saved ? JSON.parse(saved) : INITIAL_PROFILE;
  });

  const [projects, setProjects] = useState<Project[]>(() => {
    const saved = localStorage.getItem('arch_projects');
    return saved ? JSON.parse(saved) : INITIAL_PROJECTS;
  });

  const [experience, setExperience] = useState<Experience[]>(() => {
    const saved = localStorage.getItem('arch_experience');
    return saved ? JSON.parse(saved) : INITIAL_EXPERIENCE;
  });

  const [education, setEducation] = useState<Education[]>(() => {
    const saved = localStorage.getItem('arch_education');
    return saved ? JSON.parse(saved) : INITIAL_EDUCATION;
  });

  const [courses, setCourses] = useState<Course[]>(() => {
    const saved = localStorage.getItem('arch_courses');
    return saved ? JSON.parse(saved) : INITIAL_COURSES;
  });

  const [skills, setSkills] = useState<Skill[]>(() => {
    const saved = localStorage.getItem('arch_skills');
    return saved ? JSON.parse(saved) : INITIAL_SKILLS;
  });

  const [isAdmin, setIsAdmin] = useState(false);

  // Persistence Effects
  useEffect(() => { localStorage.setItem('arch_profile', JSON.stringify(profile)); }, [profile]);
  useEffect(() => { localStorage.setItem('arch_projects', JSON.stringify(projects)); }, [projects]);
  useEffect(() => { localStorage.setItem('arch_experience', JSON.stringify(experience)); }, [experience]);
  useEffect(() => { localStorage.setItem('arch_education', JSON.stringify(education)); }, [education]);
  useEffect(() => { localStorage.setItem('arch_courses', JSON.stringify(courses)); }, [courses]);
  useEffect(() => { localStorage.setItem('arch_skills', JSON.stringify(skills)); }, [skills]);

  const login = (password: string) => {
    // In a real app, verify against backend. For demo:
    if (password === '12345') {
      setIsAdmin(true);
      return true;
    }
    return false;
  };

  const logout = () => setIsAdmin(false);

  // Profile
  const updateProfile = (newProfile: ArchitectProfile) => setProfile(newProfile);

  // Projects
  const addProject = (project: Project) => setProjects(prev => [project, ...prev]);
  const updateProject = (updatedProject: Project) => {
    setProjects(prev => prev.map(p => p.id === updatedProject.id ? updatedProject : p));
  };
  const deleteProject = (id: string) => setProjects(prev => prev.filter(p => p.id !== id));

  // Experience
  const addExperience = (exp: Experience) => setExperience(prev => [exp, ...prev]);
  const updateExperience = (updatedExp: Experience) => {
    setExperience(prev => prev.map(e => e.id === updatedExp.id ? updatedExp : e));
  };
  const deleteExperience = (id: string) => setExperience(prev => prev.filter(e => e.id !== id));

  // Education
  const addEducation = (edu: Education) => setEducation(prev => [edu, ...prev]);
  const updateEducation = (updated: Education) => {
    setEducation(prev => prev.map(e => e.id === updated.id ? updated : e));
  };
  const deleteEducation = (id: string) => setEducation(prev => prev.filter(e => e.id !== id));

  // Courses
  const addCourse = (course: Course) => setCourses(prev => [course, ...prev]);
  const updateCourse = (updated: Course) => {
    setCourses(prev => prev.map(c => c.id === updated.id ? updated : c));
  };
  const deleteCourse = (id: string) => setCourses(prev => prev.filter(c => c.id !== id));

  // Skills
  const addSkill = (skill: Skill) => setSkills(prev => [...prev, skill]);
  const updateSkill = (updated: Skill) => {
    setSkills(prev => prev.map(s => s.id === updated.id ? updated : s));
  };
  const deleteSkill = (id: string) => setSkills(prev => prev.filter(s => s.id !== id));

  const resetData = () => {
    setProfile(INITIAL_PROFILE);
    setProjects(INITIAL_PROJECTS);
    setExperience(INITIAL_EXPERIENCE);
    setEducation(INITIAL_EDUCATION);
    setCourses(INITIAL_COURSES);
    setSkills(INITIAL_SKILLS);
    localStorage.clear();
    window.location.reload();
  };

  return (
    <DataContext.Provider value={{
      profile, projects, experience, education, courses, skills, isAdmin,
      login, logout, updateProfile, 
      addProject, updateProject, deleteProject,
      addExperience, updateExperience, deleteExperience,
      addEducation, updateEducation, deleteEducation,
      addCourse, updateCourse, deleteCourse,
      addSkill, updateSkill, deleteSkill,
      resetData
    }}>
      {children}
    </DataContext.Provider>
  );
};

export const useData = () => {
  const context = useContext(DataContext);
  if (!context) throw new Error("useData must be used within a DataProvider");
  return context;
};

