  try {
    // Si usas MongoDB: const experiences = await ExperienceModel.find();
    // Aqu√≠ simulamos que recuperas los datos:
    const experiences = await db.experience.findAll(); 

    // Mapeo importante para tu Frontend (si usas Mongo):
    const formatted = experiences.map(exp => ({
      id: exp._id, // Convertir _id a id
      role: exp.role,
      company: exp.company,
      period: exp.period,
      description: exp.description
    }));

    res.json(formatted);
  } catch (error) {
    res.status(500).json({ error: "Error al obtener experiencia" });
  }
