import React, { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react';
import { Project, Experience, Education, Course, Skill, Interest, ArchitectProfile } from '../types';
import { PROFILE as INITIAL_PROFILE } from '../constants';

const API_URL = 'http://localhost:4000/api/v1';

interface DataContextType {
  profile: ArchitectProfile;
  projects: Project[];
  experience: Experience[];
  education: Education[];
  courses: Course[];
  skills: Skill[];
  interests: Interest[];
  isAdmin: boolean;
  isLoading: boolean;
  login: (password: string) => Promise<boolean>;
  logout: () => void;
  updateProfile: (profile: ArchitectProfile) => Promise<void>;
  addProject: (project: Project) => Promise<void>;
  updateProject: (project: Project) => Promise<void>;
  deleteProject: (id: string) => Promise<void>;
  addExperience: (exp: Experience) => Promise<void>;
  updateExperience: (exp: Experience) => Promise<void>;
  deleteExperience: (id: string) => Promise<void>;
  addEducation: (edu: Education) => Promise<void>;
  updateEducation: (edu: Education) => Promise<void>;
  deleteEducation: (id: string) => Promise<void>;
  addCourse: (course: Course) => Promise<void>;
  updateCourse: (course: Course) => Promise<void>;
  deleteCourse: (id: string) => Promise<void>;
  addSkill: (skill: Skill) => Promise<void>;
  updateSkill: (skill: Skill) => Promise<void>;
  deleteSkill: (id: string) => Promise<void>;
  addInterest: (interest: Interest) => Promise<void>;
  updateInterest: (interest: Interest) => Promise<void>;
  deleteInterest: (id: string) => Promise<void>;
  resetData: () => void;
}

const DataContext = createContext<DataContextType | undefined>(undefined);

export const DataProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [profile, setProfile] = useState<ArchitectProfile>(INITIAL_PROFILE);
  const [projects, setProjects] = useState<Project[]>([]);
  const [experience, setExperience] = useState<Experience[]>([]);
  const [education, setEducation] = useState<Education[]>([]);
  const [courses, setCourses] = useState<Course[]>([]);
  const [skills, setSkills] = useState<Skill[]>([]);
  const [interests, setInterests] = useState<Interest[]>([]);

  const [isAdmin, setIsAdmin] = useState(false);
  const [token, setToken] = useState<string | null>(localStorage.getItem('token'));
  const [isLoading, setIsLoading] = useState(true);

  // Helper for authorized fetches
  const authFetch = useCallback(async (endpoint: string, options: RequestInit = {}) => {
    const headers = {
      'Content-Type': 'application/json',
      ...(token ? { 'Authorization': `Bearer ${token}` } : {}),
      ...options.headers,
    };
    const response = await fetch(`${API_URL}${endpoint}`, { ...options, headers });
    if (!response.ok) {
      throw new Error(`API Error: ${response.statusText}`);
    }
    return response.json();
  }, [token]);

  // Load Initial Data
  const fetchData = useCallback(async () => {
    try {
      setIsLoading(true);
      const [profData, projData, expData, eduData, courseData, skillData, intData] = await Promise.all([
        fetch(`${API_URL}/profile`).then(res => res.json()),
        fetch(`${API_URL}/projects`).then(res => res.json()),
        fetch(`${API_URL}/experience`).then(res => res.json()),
        fetch(`${API_URL}/education`).then(res => res.json()),
        fetch(`${API_URL}/courses`).then(res => res.json()),
        fetch(`${API_URL}/skills`).then(res => res.json()),
        fetch(`${API_URL}/interests`).then(res => res.json()),
      ]);

