  };

  // --- Profile ---
  const updateProfile = async (newProfile: ArchitectProfile) => {
    try {
      const updated = await authFetch('/profile', {
        method: 'PUT',
        body: JSON.stringify(newProfile)
      });
      setProfile(updated);
    } catch (e) { console.error(e); }
  };

  // --- Projects ---
  const addProject = async (project: Project) => {
    try {
      // Remove ID if it's a temporary frontend ID, or let backend handle it
      const { id, ...data } = project; 
      const newProject = await authFetch('/projects', {
        method: 'POST',
        body: JSON.stringify(data)
      });
      setProjects(prev => [newProject, ...prev]);
    } catch (e) { console.error(e); }
  };

  const updateProject = async (project: Project) => {
    try {
      const updated = await authFetch(`/projects/${project.id}`, {
        method: 'PUT',
        body: JSON.stringify(project)
      });
      setProjects(prev => prev.map(p => p.id === updated.id ? updated : p));
    } catch (e) { console.error(e); }
  };

