import React, { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react';
import { Project, Experience, Education, Course, Skill, Interest, ArchitectProfile } from '../types';
import { PROFILE as INITIAL_PROFILE } from '../constants';

const API_URL = 'https://arquinory.vercel.app/api/v1';

interface DataContextType {
  profile: ArchitectProfile;
  projects: Project[];
  experience: Experience[];
  education: Education[];
  courses: Course[];
  skills: Skill[];
  interests: Interest[];
  isAdmin: boolean;
  isLoading: boolean;
  login: (password: string) => Promise<boolean>;
  logout: () => void;
  updateProfile: (profile: ArchitectProfile) => Promise<void>;
  addProject: (project: Project) => Promise<void>;
  updateProject: (project: Project) => Promise<void>;
  deleteProject: (id: string) => Promise<void>;
  addExperience: (exp: Experience) => Promise<void>;
  updateExperience: (exp: Experience) => Promise<void>;
  deleteExperience: (id: string) => Promise<void>;
  addEducation: (edu: Education) => Promise<void>;
  updateEducation: (edu: Education) => Promise<void>;
  deleteEducation: (id: string) => Promise<void>;
  addCourse: (course: Course) => Promise<void>;
  updateCourse: (course: Course) => Promise<void>;
  deleteCourse: (id: string) => Promise<void>;
  addSkill: (skill: Skill) => Promise<void>;
  updateSkill: (skill: Skill) => Promise<void>;
  deleteSkill: (id: string) => Promise<void>;
  addInterest: (interest: Interest) => Promise<void>;
  updateInterest: (interest: Interest) => Promise<void>;
  deleteInterest: (id: string) => Promise<void>;
  resetData: () => void;
}

const DataContext = createContext<DataContextType | undefined>(undefined);

export const DataProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [profile, setProfile] = useState<ArchitectProfile>(INITIAL_PROFILE);
  const [projects, setProjects] = useState<Project[]>([]);
  const [experience, setExperience] = useState<Experience[]>([]);
  const [education, setEducation] = useState<Education[]>([]);
  const [courses, setCourses] = useState<Course[]>([]);
  const [skills, setSkills] = useState<Skill[]>([]);
  const [interests, setInterests] = useState<Interest[]>([]);

  const [isAdmin, setIsAdmin] = useState(false);
  const [token, setToken] = useState<string | null>(localStorage.getItem('token'));
  const [isLoading, setIsLoading] = useState(true);

  // Helper for authorized fetches
  const authFetch = useCallback(async (endpoint: string, options: RequestInit = {}) => {
    const headers = {
      'Content-Type': 'application/json',
      ...(token ? { 'Authorization': `Bearer ${token}` } : {}),
      ...options.headers,
    };
    const response = await fetch(`${API_URL}${endpoint}`, { ...options, headers });
    if (!response.ok) {
      throw new Error(`API Error: ${response.statusText}`);
    }
    return response.json();
  }, [token]);

  // Load Initial Data
  const fetchData = useCallback(async () => {
    try {
      setIsLoading(true);
      const [profData, projData, expData, eduData, courseData, skillData, intData] = await Promise.all([
        fetch(`${API_URL}/profile`).then(res => res.json()),
        fetch(`${API_URL}/projects`).then(res => res.json()),
        fetch(`${API_URL}/experience`).then(res => res.json()),
        fetch(`${API_URL}/education`).then(res => res.json()),
        fetch(`${API_URL}/courses`).then(res => res.json()),
        fetch(`${API_URL}/skills`).then(res => res.json()),
        fetch(`${API_URL}/interests`).then(res => res.json()),
      ]);

      if (profData) setProfile(profData);
      if (Array.isArray(projData)) setProjects(projData);
      if (Array.isArray(expData)) setExperience(expData);
      if (Array.isArray(eduData)) setEducation(eduData);
      if (Array.isArray(courseData)) setCourses(courseData);
      if (Array.isArray(skillData)) setSkills(skillData);
      if (Array.isArray(intData)) setInterests(intData);
    } catch (error) {
      console.error("Failed to fetch data", error);
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  useEffect(() => {
    if (token) {
      setIsAdmin(true);
    }
  }, [token]);

  // --- Auth ---
  const login = async (password: string) => {
    try {
      const res = await fetch(`${API_URL}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ password })
      });

      if (res.ok) {
        const data = await res.json();
        // Assuming backend returns { token: "..." }
        if (data.token) {
          setToken(data.token);
          localStorage.setItem('token', data.token);
          setIsAdmin(true);
          return true;
        }
      }
      return false;
    } catch (err) {
      console.error(err);
      return false;
    }
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem('token');
    setIsAdmin(false);
  };

  // --- Profile ---
  const updateProfile = async (newProfile: ArchitectProfile) => {
    try {
      const updated = await authFetch('/profile', {
        method: 'PUT',
        body: JSON.stringify(newProfile)
      });
      setProfile(updated);
    } catch (e) { console.error(e); }
  };

  // --- Projects ---
  const addProject = async (project: Project) => {
    try {
      // Remove ID if it's a temporary frontend ID, or let backend handle it
      const { id, ...data } = project; 
      const newProject = await authFetch('/projects', {
        method: 'POST',
        body: JSON.stringify(data)
      });
      setProjects(prev => [newProject, ...prev]);
    } catch (e) { console.error(e); }
  };

  const updateProject = async (project: Project) => {
    try {
      const updated = await authFetch(`/projects/${project.id}`, {
        method: 'PUT',
        body: JSON.stringify(project)
      });
      setProjects(prev => prev.map(p => p.id === updated.id ? updated : p));
    } catch (e) { console.error(e); }
  };

  const deleteProject = async (id: string) => {
    try {
      await authFetch(`/projects/${id}`, { method: 'DELETE' });
      setProjects(prev => prev.filter(p => p.id !== id));
    } catch (e) { console.error(e); }
  };

  // --- Experience ---
  const addExperience = async (exp: Experience) => {
    try {
      const { id, ...data } = exp;
      const newExp = await authFetch('/experience', {
        method: 'POST',
        body: JSON.stringify(data)
      });
      setExperience(prev => [newExp, ...prev]);
    } catch (e) { console.error(e); }
  };

  const updateExperience = async (exp: Experience) => {
    try {
      const updated = await authFetch(`/experience/${exp.id}`, {
        method: 'PUT',
        body: JSON.stringify(exp)
      });
      setExperience(prev => prev.map(e => e.id === updated.id ? updated : e));
    } catch (e) { console.error(e); }
  };

  const deleteExperience = async (id: string) => {
    try {
      await authFetch(`/experience/${id}`, { method: 'DELETE' });
      setExperience(prev => prev.filter(e => e.id !== id));
    } catch (e) { console.error(e); }
  };

  // --- Education ---
  const addEducation = async (edu: Education) => {
    try {
      const { id, ...data } = edu;
      const newEdu = await authFetch('/education', {
        method: 'POST',
        body: JSON.stringify(data)
      });
      setEducation(prev => [newEdu, ...prev]);
    } catch (e) { console.error(e); }
  };

  const updateEducation = async (edu: Education) => {
    try {
      const updated = await authFetch(`/education/${edu.id}`, {
        method: 'PUT',
        body: JSON.stringify(edu)
      });
      setEducation(prev => prev.map(e => e.id === updated.id ? updated : e));
    } catch (e) { console.error(e); }
  };

  const deleteEducation = async (id: string) => {
    try {
      await authFetch(`/education/${id}`, { method: 'DELETE' });
      setEducation(prev => prev.filter(e => e.id !== id));
    } catch (e) { console.error(e); }
  };

  // --- Courses ---
  const addCourse = async (course: Course) => {
    try {
      const { id, ...data } = course;
      const newCourse = await authFetch('/courses', {
        method: 'POST',
        body: JSON.stringify(data)
      });
      setCourses(prev => [newCourse, ...prev]);
    } catch (e) { console.error(e); }
  };

  const updateCourse = async (course: Course) => {
    try {
      const updated = await authFetch(`/courses/${course.id}`, {
        method: 'PUT',
        body: JSON.stringify(course)
      });
      setCourses(prev => prev.map(c => c.id === updated.id ? updated : c));
    } catch (e) { console.error(e); }
  };

  const deleteCourse = async (id: string) => {
    try {
      await authFetch(`/courses/${id}`, { method: 'DELETE' });
      setCourses(prev => prev.filter(c => c.id !== id));
    } catch (e) { console.error(e); }
  };

  // --- Skills ---
  const addSkill = async (skill: Skill) => {
    try {
      const { id, ...data } = skill;
      const newSkill = await authFetch('/skills', {
        method: 'POST',
        body: JSON.stringify(data)
      });
      setSkills(prev => [...prev, newSkill]);
    } catch (e) { console.error(e); }
  };

  const updateSkill = async (skill: Skill) => {
    try {
      const updated = await authFetch(`/skills/${skill.id}`, {
        method: 'PUT',
        body: JSON.stringify(skill)
      });
      setSkills(prev => prev.map(s => s.id === updated.id ? updated : s));
    } catch (e) { console.error(e); }
  };

  const deleteSkill = async (id: string) => {
    try {
      await authFetch(`/skills/${id}`, { method: 'DELETE' });
      setSkills(prev => prev.filter(s => s.id !== id));
    } catch (e) { console.error(e); }
  };

  // --- Interests ---
  const addInterest = async (interest: Interest) => {
    try {
      const { id, ...data } = interest;
      const newInt = await authFetch('/interests', {
        method: 'POST',
        body: JSON.stringify(data)
      });
      setInterests(prev => [...prev, newInt]);
    } catch (e) { console.error(e); }
  };

  const updateInterest = async (interest: Interest) => {
    try {
      const updated = await authFetch(`/interests/${interest.id}`, {
        method: 'PUT',
        body: JSON.stringify(interest)
      });
      setInterests(prev => prev.map(i => i.id === updated.id ? updated : i));
    } catch (e) { console.error(e); }
  };

  const deleteInterest = async (id: string) => {
    try {
      await authFetch(`/interests/${id}`, { method: 'DELETE' });
      setInterests(prev => prev.filter(i => i.id !== id));
    } catch (e) { console.error(e); }
  };

  const resetData = () => {
    logout();
    fetchData(); // Reload original data from API
  };

  return (
    <DataContext.Provider value={{
      profile, projects, experience, education, courses, skills, interests, isAdmin, isLoading,
      login, logout, updateProfile, 
      addProject, updateProject, deleteProject,
      addExperience, updateExperience, deleteExperience,
      addEducation, updateEducation, deleteEducation,
      addCourse, updateCourse, deleteCourse,
      addSkill, updateSkill, deleteSkill,
      addInterest, updateInterest, deleteInterest,
      resetData
    }}>
      {children}
    </DataContext.Provider>
  );
};

export const useData = () => {
  const context = useContext(DataContext);
  if (!context) throw new Error("useData must be used within a DataProvider");
  return context;
};

