      if (Array.isArray(expData)) setExperience(expData);
      if (Array.isArray(eduData)) setEducation(eduData);
      if (Array.isArray(courseData)) setCourses(courseData);
      if (Array.isArray(skillData)) setSkills(skillData);
      if (Array.isArray(intData)) setInterests(intData);

    } catch (error) {
      console.warn("API Connection failed (CORS, Offline, or 404). Loading local backup data.", error);

      // FALLBACK: Load local constants if API fails so the site never looks broken
      setProfile(INITIAL_PROFILE);
      setProjects(INITIAL_PROJECTS);
      setExperience(INITIAL_EXPERIENCE);
      setEducation(INITIAL_EDUCATION);
      setCourses(INITIAL_COURSES);
      setSkills(INITIAL_SKILLS);
      setInterests(INITIAL_INTERESTS);
    } finally {
      setIsLoading(false);
    }
